let callForm = function(e) {
    e.preventDefault;
    e.target.classList.remove('visible_form');

    formWrapper.classList.add('visible_form');
    formContact.classList.add('visible_form');
    console.log('visible');
}

for(let i = 0; i < writeUs.length; i++) {
    writeUs[i].addEventListener('click', callForm, false);
}

let closeForm = function(e) {
    e.preventDefault;
    formWrapper.classList.remove('visible_form');
}

for(let i = 0; i < formClose.length; i++) {
    formClose[i].addEventListener('click', closeForm, false);
}

for(let i = 0; i < nav.length; i++) {
    let oneNav = nav[i];
    let callMenu = function(e) {
        e.preventDefault;
        brand.classList.toggle('visible_form');
        oneNav.classList.toggle('visible_menu');
        menu[0].classList.toggle('col-9');
        menu[0].classList.toggle('col-12');
        menu[0].classList.toggle('col-md-3');
        navigate.classList.toggle('height-max');
        burgerPlank[0].classList.toggle('close-menu');
        burger[0].classList.toggle('close-menu_top');
        for(let i = 0; i < burgerPlank.length; i++) {
            burger[i].addEventListener('click', callMenu, false);
        }
    }
    
    for(let i = 0; i < burger.length; i++) {
        burger[i].addEventListener('click', callMenu, false);
    }
};

